import React from 'react';
import { Bar, BarChart, CartesianGrid, Legend, ResponsiveContainer, Tooltip, XAxis, YAxis } from 'recharts';
import { DebtMapEntry } from '../types';

interface DebtMapBarChartProps {
    data: DebtMapEntry[];
    selectedBanks: string[];
}

export const DebtMapBarChart: React.FC<DebtMapBarChartProps> = ({ data, selectedBanks }) => {
    // Filter data for selected banks and only show yearly data
    const yearlyData = data
        .filter(entry => selectedBanks.includes(entry.Banco) && entry.M√™s % 12 === 0)
        .map(entry => ({
            ...entry,
            Ano: entry.Ano
        }));

    // Group by year and bank
    const chartData: any[] = [];
    const years = Array.from(new Set(yearlyData.map(d => d.Ano))).sort((a: number, b: number) => a - b);

    years.forEach(year => {
        const yearData: any = { Ano: year };
        selectedBanks.forEach(bank => {
            const bankData = yearlyData.find(d => d.Banco === bank && d.Ano === year);
            if (bankData) {
                yearData[`${bank}_Capital`] = bankData["Capital em D√≠vida (‚Ç¨)"];
                yearData[`${bank}_Comissoes`] = bankData["Comiss√µes Acumuladas (‚Ç¨)"];
            }
        });
        chartData.push(yearData);
    });

    const colors = ['#3b82f6', '#ef4444', '#10b981', '#f59e0b', '#8b5cf6', '#06b6d4', '#84cc16', '#f97316'];

    const formatCurrency = (value: any) => {
        if (typeof value === 'number') {
            return new Intl.NumberFormat('pt-PT', {
                style: 'currency',
                currency: 'EUR'
            }).format(value);
        }
        return value;
    };

    return (
        <div className="space-y-8">
            {/* Capital em D√≠vida Bar Chart */}
            <div className="bg-white rounded-xl border border-gray-200 p-6">
                <h3 className="text-xl font-semibold text-gray-900 mb-6 flex items-center space-x-2">
                    <span className="text-2xl">üìä</span>
                    <span>Capital em D√≠vida por Ano</span>
                </h3>
                <ResponsiveContainer width="100%" height={400}>
                    <BarChart data={chartData} margin={{ top: 20, right: 30, left: 20, bottom: 20 }}>
                        <CartesianGrid strokeDasharray="3 3" stroke="#e5e7eb" />
                        <XAxis
                            dataKey="Ano"
                            label={{ value: 'Ano', position: 'insideBottom', offset: -10 }}
                            tick={{ fontSize: 12 }}
                        />
                        <YAxis
                            label={{ value: 'Capital em D√≠vida (‚Ç¨)', angle: -90, position: 'insideLeft' }}
                            tickFormatter={(value) => `${(value / 1000).toFixed(0)}k`}
                            tick={{ fontSize: 12 }}
                        />
                        <Tooltip
                            formatter={(value: any) => [formatCurrency(value), 'Capital em D√≠vida']}
                            labelFormatter={(label) => `Ano ${label}`}
                            contentStyle={{
                                backgroundColor: 'white',
                                border: '1px solid #e5e7eb',
                                borderRadius: '8px',
                                boxShadow: '0 4px 6px -1px rgba(0, 0, 0, 0.1)'
                            }}
                        />
                        <Legend />
                        {selectedBanks.map((bank, index) => (
                            <Bar
                                key={`${bank}_Capital`}
                                dataKey={`${bank}_Capital`}
                                name={`${bank}`}
                                fill={colors[index % colors.length]}
                                radius={[4, 4, 0, 0]}
                            />
                        ))}
                    </BarChart>
                </ResponsiveContainer>
            </div>

            {/* Comiss√µes Acumuladas Bar Chart */}
            <div className="bg-white rounded-xl border border-gray-200 p-6">
                <h3 className="text-xl font-semibold text-gray-900 mb-6 flex items-center space-x-2">
                    <span className="text-2xl">üìà</span>
                    <span>Comiss√µes Acumuladas por Ano</span>
                </h3>
                <ResponsiveContainer width="100%" height={400}>
                    <BarChart data={chartData} margin={{ top: 20, right: 30, left: 20, bottom: 20 }}>
                        <CartesianGrid strokeDasharray="3 3" stroke="#e5e7eb" />
                        <XAxis
                            dataKey="Ano"
                            label={{ value: 'Ano', position: 'insideBottom', offset: -10 }}
                            tick={{ fontSize: 12 }}
                        />
                        <YAxis
                            label={{ value: 'Comiss√µes Acumuladas (‚Ç¨)', angle: -90, position: 'insideLeft' }}
                            tickFormatter={(value) => `${(value / 1000).toFixed(0)}k`}
                            tick={{ fontSize: 12 }}
                        />
                        <Tooltip
                            formatter={(value: any) => [formatCurrency(value), 'Comiss√µes Acumuladas']}
                            labelFormatter={(label) => `Ano ${label}`}
                            contentStyle={{
                                backgroundColor: 'white',
                                border: '1px solid #e5e7eb',
                                borderRadius: '8px',
                                boxShadow: '0 4px 6px -1px rgba(0, 0, 0, 0.1)'
                            }}
                        />
                        <Legend />
                        {selectedBanks.map((bank, index) => (
                            <Bar
                                key={`${bank}_Comissoes`}
                                dataKey={`${bank}_Comissoes`}
                                name={`${bank}`}
                                fill={colors[index % colors.length]}
                                radius={[4, 4, 0, 0]}
                            />
                        ))}
                    </BarChart>
                </ResponsiveContainer>
            </div>

            {/* Compara√ß√£o Anual - Capital vs Comiss√µes */}
            <div className="bg-white rounded-xl border border-gray-200 p-6">
                <h3 className="text-xl font-semibold text-gray-900 mb-6 flex items-center space-x-2">
                    <span className="text-2xl">‚öñÔ∏è</span>
                    <span>Compara√ß√£o Anual: Capital vs Comiss√µes</span>
                </h3>
                <ResponsiveContainer width="100%" height={400}>
                    <BarChart data={chartData} margin={{ top: 20, right: 30, left: 20, bottom: 20 }}>
                        <CartesianGrid strokeDasharray="3 3" stroke="#e5e7eb" />
                        <XAxis
                            dataKey="Ano"
                            label={{ value: 'Ano', position: 'insideBottom', offset: -10 }}
                            tick={{ fontSize: 12 }}
                        />
                        <YAxis
                            label={{ value: 'Valor (‚Ç¨)', angle: -90, position: 'insideLeft' }}
                            tickFormatter={(value) => `${(value / 1000).toFixed(0)}k`}
                            tick={{ fontSize: 12 }}
                        />
                        <Tooltip
                            formatter={(value: any, name: any) => [
                                formatCurrency(value),
                                name.includes('Capital') ? 'Capital em D√≠vida' : 'Comiss√µes Acumuladas'
                            ]}
                            labelFormatter={(label) => `Ano ${label}`}
                            contentStyle={{
                                backgroundColor: 'white',
                                border: '1px solid #e5e7eb',
                                borderRadius: '8px',
                                boxShadow: '0 4px 6px -1px rgba(0, 0, 0, 0.1)'
                            }}
                        />
                        <Legend />
                        {selectedBanks.map((bank, index) => (
                            <React.Fragment key={bank}>
                                <Bar
                                    dataKey={`${bank}_Capital`}
                                    name={`${bank} - Capital`}
                                    fill={colors[index % colors.length]}
                                    radius={[4, 4, 0, 0]}
                                    opacity={0.8}
                                />
                                <Bar
                                    dataKey={`${bank}_Comissoes`}
                                    name={`${bank} - Comiss√µes`}
                                    fill={colors[(index + 4) % colors.length]}
                                    radius={[4, 4, 0, 0]}
                                    opacity={0.6}
                                />
                            </React.Fragment>
                        ))}
                    </BarChart>
                </ResponsiveContainer>
            </div>
        </div>
    );
};
