import { BarChart3, Calendar, Smartphone } from 'lucide-react';
import React, { useState } from 'react';
import { DebtMapEntry } from '../types';

interface YearlyCalculationsTableProps {
    data: DebtMapEntry[];
    selectedBanks: string[];
}

export const YearlyCalculationsTable: React.FC<YearlyCalculationsTableProps> = ({
    data,
    selectedBanks
}) => {
    const [viewMode, setViewMode] = useState<'years' | 'months'>('years');
    const [selectedYear, setSelectedYear] = useState<string>('todos');
    const [activeBankTab, setActiveBankTab] = useState<string>('todos');
    const [selectedBanksForFilter, setSelectedBanksForFilter] = useState<Set<string>>(new Set());

    const formatCurrency = (value: number) => {
        return new Intl.NumberFormat('pt-PT', {
            style: 'currency',
            currency: 'EUR'
        }).format(value);
    };

    // Get unique years from data
    const years = Array.from(new Set(data.map(d => d.Ano))).sort((a: number, b: number) => a - b);

    // Filter data based on selected year
    const yearFilteredData = selectedYear === 'todos'
        ? data
        : data.filter(d => d.Ano === parseInt(selectedYear));

    // Filter data based on selected banks
    const bankFilteredData = selectedBanksForFilter.size === 0
        ? yearFilteredData
        : yearFilteredData.filter(d => selectedBanksForFilter.has(d.Banco));

    const handleBankSelection = (bankName: string) => {
        const newSelectedBanks = new Set(selectedBanksForFilter);

        if (bankName === 'todos') {
            // Se clicar em "Todos", limpa a seleção
            setSelectedBanksForFilter(new Set());
            setActiveBankTab('todos');
        } else {
            // Se clicar em um banco específico
            if (newSelectedBanks.has(bankName)) {
                // Remove o banco se já estiver selecionado
                newSelectedBanks.delete(bankName);
                if (newSelectedBanks.size === 0) {
                    setActiveBankTab('todos');
                }
            } else {
                // Adiciona o banco se não estiver selecionado
                newSelectedBanks.add(bankName);
                setActiveBankTab(bankName);
            }
            setSelectedBanksForFilter(newSelectedBanks);
        }
    };

    const renderTable = (bankFilter: string | null) => {
        // Use bankFilteredData instead of filtering by activeBankTab
        const tableData = bankFilteredData;

        if (tableData.length === 0) {
            return (
                <div className="text-center py-12">
                    <div className="w-16 h-16 mx-auto mb-4 bg-gray-100 rounded-full flex items-center justify-center">
                        <BarChart3 className="h-8 w-8 text-gray-400" />
                    </div>
                    <h3 className="text-lg font-semibold text-gray-900 mb-2">Nenhum dado encontrado</h3>
                    <p className="text-gray-600">Tente ajustar os filtros para ver os dados.</p>
                </div>
            );
        }

        // Group data by year
        const years = Array.from(new Set(tableData.map(d => d.Ano))).sort((a: number, b: number) => a - b);

        return (
            <div className="space-y-6">
                {years.map(year => {
                    const yearData = tableData.filter(d => d.Ano === year);
                    return (
                        <div key={year} className="bg-white rounded-xl shadow-sm border border-gray-200 overflow-hidden">
                            <div className="bg-gradient-to-r from-blue-50 to-indigo-50 px-6 py-4 border-b border-gray-200">
                                <h3 className="text-lg font-semibold text-blue-900 flex items-center space-x-2">
                                    <Calendar className="h-5 w-5" />
                                    <span>
                                        {viewMode === 'years' ? `${year}º Ano` : `${year}º Ano`}
                                    </span>
                                </h3>
                            </div>

                            <div className="overflow-x-auto rounded-lg">
                                <table className="w-full min-w-[500px]">
                                    <thead>
                                        <tr className="bg-gray-50 border-b border-gray-200">
                                            <th className="text-left px-1 md:px-4 py-1 md:py-3 text-[10px] md:text-sm font-semibold text-gray-700 sticky top-0 bg-gray-50 z-10">Banco</th>
                                            {viewMode === 'months' && (
                                                <th className="text-center px-0.5 md:px-4 py-1 md:py-3 text-[10px] md:text-sm font-semibold text-gray-700 sticky top-0 bg-gray-50 z-10">Mês</th>
                                            )}
                                            <th className="text-right px-0.5 md:px-4 py-1 md:py-3 text-[10px] md:text-sm font-semibold text-gray-700 sticky top-0 bg-gray-50 z-10">Prestação Base</th>
                                            <th className="text-right px-0.5 md:px-4 py-1 md:py-3 text-[10px] md:text-sm font-semibold text-gray-700 sticky top-0 bg-gray-50 z-10">Prestação Total</th>
                                            <th className="text-right px-0.5 md:px-4 py-1 md:py-3 text-[10px] md:text-sm font-semibold text-gray-700 sticky top-0 bg-gray-50 z-10 hidden md:table-cell">Dívida</th>
                                            <th className="text-right px-1 md:px-4 py-1 md:py-3 text-[10px] md:text-sm font-semibold text-gray-700 sticky top-0 bg-gray-50 z-10">CashOut</th>
                                        </tr>
                                    </thead>
                                    <tbody className="divide-y divide-gray-100">
                                        {yearData.map((row: any, index: number) => (
                                            <tr key={`${row.Banco}-${row.Ano}-${row.Mês || ''}`} className="hover:bg-gray-50 transition-colors">
                                                <td className="px-1 md:px-4 py-1 md:py-3 whitespace-nowrap">
                                                    <span className="font-medium text-gray-900 text-[10px] md:text-base break-all">{row.Banco}</span>
                                                </td>
                                                {viewMode === 'months' && (
                                                    <td className="px-0.5 md:px-4 py-1 md:py-3 text-center text-[10px] md:text-sm text-gray-600 font-medium">
                                                        {row.Mês}
                                                    </td>
                                                )}
                                                <td className="px-0.5 md:px-4 py-1 md:py-3 text-right text-[10px] md:text-sm font-medium text-gray-900">{formatCurrency(row["Prestação (€)"])}</td>
                                                <td className="px-0.5 md:px-4 py-1 md:py-3 text-right text-[10px] md:text-sm font-medium text-gray-900">{formatCurrency(row["Total Mensal (€)"])}</td>
                                                <td className="px-0.5 md:px-4 py-1 md:py-3 text-right text-[10px] md:text-sm font-medium text-gray-900 hidden md:table-cell">{formatCurrency(row["Capital em Dívida (€)"])}</td>
                                                <td className="px-1 md:px-4 py-1 md:py-3 text-right text-[10px] md:text-sm font-bold text-gray-900">{formatCurrency(row["CashOut Líquido (€)"])}</td>
                                            </tr>
                                        ))}
                                    </tbody>
                                </table>
                            </div>
                        </div>
                    );
                })}
            </div>
        );
    };

    return (
        <div className="space-y-6">
            {/* Professional Header */}
            <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-6">
                <div className="flex items-center justify-between mb-4">
                    <div className="flex items-center">
                        <div className="w-10 h-10 bg-blue-100 rounded-lg flex items-center justify-center mr-4">
                            <BarChart3 className="h-5 w-5 text-blue-600" />
                        </div>
                        <div>
                            <h2 className="text-xl font-bold text-gray-900">Mapa de Dívida</h2>
                            <p className="text-sm text-gray-600">Análise detalhada da evolução do empréstimo</p>
                        </div>
                    </div>
                    <div className="hidden sm:flex items-center text-sm text-gray-500">
                        <Smartphone className="h-4 w-4 mr-2" />
                        {selectedBanksForFilter.size > 0 ? selectedBanksForFilter.size : selectedBanks.length} banco{(selectedBanksForFilter.size > 0 ? selectedBanksForFilter.size : selectedBanks.length) !== 1 ? 's' : ''} selecionado{(selectedBanksForFilter.size > 0 ? selectedBanksForFilter.size : selectedBanks.length) !== 1 ? 's' : ''}
                    </div>
                </div>

                {/* Controls */}
                <div className="grid grid-cols-1 lg:grid-cols-3 gap-4">
                    {/* View Mode */}
                    <div className="space-y-2">
                        <label className="text-sm font-medium text-gray-700">Modo de Visualização</label>
                        <div className="flex rounded-lg border border-gray-300 p-1 bg-gray-50">
                            <button
                                onClick={() => setViewMode('years')}
                                className={`flex-1 px-3 py-2 text-sm font-medium rounded-md transition-all ${viewMode === 'years'
                                    ? 'bg-white text-blue-600 shadow-sm border border-gray-200'
                                    : 'text-gray-600 hover:text-gray-900'
                                    }`}
                            >
                                Anos
                            </button>
                            <button
                                onClick={() => setViewMode('months')}
                                className={`flex-1 px-3 py-2 text-sm font-medium rounded-md transition-all ${viewMode === 'months'
                                    ? 'bg-white text-blue-600 shadow-sm border border-gray-200'
                                    : 'text-gray-600 hover:text-gray-900'
                                    }`}
                            >
                                Meses
                            </button>
                        </div>
                    </div>

                    {/* Year Filter */}
                    <div className="space-y-2">
                        <label className="text-sm font-medium text-gray-700">Filtrar por Ano</label>
                        <select
                            value={selectedYear}
                            onChange={(e) => setSelectedYear(e.target.value)}
                            className="w-full px-3 py-2 text-sm border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 bg-white"
                        >
                            <option value="todos">Todos os anos</option>
                            {years.map(year => (
                                <option key={year} value={year}>{year}º ano</option>
                            ))}
                        </select>
                    </div>

                    {/* Bank Filter - Multiple Selection */}
                    <div className="space-y-2">
                        <label className="text-sm font-medium text-gray-700">Filtrar por Banco (Múltipla Seleção)</label>
                        <div className="flex flex-wrap gap-2">
                            <button
                                onClick={() => handleBankSelection('todos')}
                                className={`px-3 py-2 text-sm font-medium rounded-lg border transition-all ${selectedBanksForFilter.size === 0
                                    ? 'bg-blue-600 text-white border-blue-600'
                                    : 'bg-white text-gray-700 border-gray-300 hover:bg-gray-50'
                                    }`}
                            >
                                Todos
                            </button>
                            {selectedBanks.map(bankName => (
                                <button
                                    key={bankName}
                                    onClick={() => handleBankSelection(bankName)}
                                    className={`px-3 py-2 text-sm font-medium rounded-lg border transition-all ${selectedBanksForFilter.has(bankName)
                                        ? 'bg-blue-600 text-white border-blue-600'
                                        : 'bg-white text-gray-700 border-gray-300 hover:bg-gray-50'
                                        }`}
                                >
                                    {bankName}
                                </button>
                            ))}
                        </div>
                        {selectedBanksForFilter.size > 0 && (
                            <p className="text-xs text-gray-500 mt-1">
                                Bancos selecionados: {Array.from(selectedBanksForFilter).join(', ')}
                            </p>
                        )}
                    </div>
                </div>
            </div>

            {/* Table */}
            {renderTable(null)}
        </div>
    );
};
