import { Globe } from 'lucide-react';
import React from 'react';
import { useTranslation } from 'react-i18next';

const LanguageSwitcher: React.FC = () => {
    const { i18n } = useTranslation();

    const changeLanguage = (lng: string) => {
        i18n.changeLanguage(lng);
    };

    const currentLanguage = i18n.language;

    return (
        <div className="relative group">
            <button
                className="flex items-center space-x-2 px-3 py-2 rounded-lg text-sm font-medium text-gray-600 hover:text-gray-900 hover:bg-gray-50 transition-all duration-200 border border-gray-200"
                onClick={() => {
                    const newLang = currentLanguage === 'pt' ? 'en' : 'pt';
                    changeLanguage(newLang);
                }}
            >
                <Globe className="h-4 w-4" />
                <span className="uppercase font-semibold">
                    {currentLanguage === 'pt' ? 'PT' : 'EN'}
                </span>
            </button>
        </div>
    );
};

export default LanguageSwitcher;
