                                <Line
                                    type="monotone"
                                    dataKey={`${bank}_CashOutBruto`}
                                    name={`${bank} - Bruto`}
                                    stroke={colors[index % colors.length]}
                                    strokeWidth={3}
                                    dot={{ r: 6, strokeWidth: 2, fill: 'white' }}
                                    activeDot={{ r: 8, strokeWidth: 3 }}
                                />
                                <Line
                                    type="monotone"
                                    dataKey={`${bank}_CashOutLiquido`}
                                    name={`${bank} - Líquido`}
                                    stroke={colors[index % colors.length]}
                                    strokeWidth={3}
                                    strokeDasharray="5 5"
                                    dot={{ r: 6, strokeWidth: 2, fill: 'white' }}
                                    activeDot={{ r: 8, strokeWidth: 3 }}
                                />
                            </React.Fragment>
